"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import {
  Weight,
  CheckCircle,
  Clock,
  ChevronUp,
  ChevronDown,
  ChevronsUpDown,
} from "lucide-react";
import { cn } from "@/lib/utils";

interface WeighingRecord {
  id: number;
  date: string;
  time: string;
  plate: string;
  driver: string;
  company: string;
  cargo: string;
  entryWeight: string;
  exitWeight: string;
  netWeight: string;
  status: string;
}

interface SortConfig {
  key: string;
  direction: "asc" | "desc";
}

interface WeighingTableProps {
  records: WeighingRecord[];
  sortConfig: SortConfig;
  onSort: (key: string) => void;
}

const tableCols = [
  { label: "Data/Hora", key: "date" },
  { label: "Caminhão", key: "plate" },
  { label: "Motorista", key: "driver" },
  { label: "Empresa", key: "company" },
  { label: "Carga", key: "cargo" },
  { label: "Entrada (kg)", key: "entryWeight" },
  { label: "Saída (kg)", key: "exitWeight" },
  { label: "Líquido (kg)", key: "netWeight" },
  { label: "Status", key: "status" },
];

export function WeighingTable({ records, sortConfig, onSort }: WeighingTableProps) {
  const getSortIcon = (columnKey: string) => {
    if (sortConfig.key !== columnKey) {
      return <ChevronsUpDown className="h-4 w-4 text-gray-400" />;
    }
    return sortConfig.direction === "asc" 
      ? <ChevronUp className="h-4 w-4 text-primary-600" />
      : <ChevronDown className="h-4 w-4 text-primary-600" />;
  };

  return (
    <Card className="flex-1">
      <CardHeader>
        <CardTitle className="text-lg font-semibold flex items-center gap-2">
          <Weight className="h-5 w-5 text-primary-600" />
          Registros de Pesagem
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div className="overflow-x-auto rounded-2xl">
          <Table>
            <TableHeader>
              <TableRow>
                {tableCols.map((col, index) => (
                  <TableHead 
                    key={index} 
                    className="cursor-pointer hover:bg-gray-50 select-none"
                    onClick={() => onSort(col.key)}
                  >
                    <div className="flex items-center justify-between">
                      {col.label}
                      {getSortIcon(col.key)}
                    </div>
                  </TableHead>
                ))}
              </TableRow>
            </TableHeader>
            <TableBody>
              {records.map((record) => (
                <TableRow key={record.id}>
                  <TableCell>
                    <div className="text-sm">
                      <div className="font-medium">{record.date}</div>
                      <div className="text-gray-500">{record.time}</div>
                    </div>
                  </TableCell>
                  <TableCell className="font-medium">
                    {record.plate}
                  </TableCell>
                  <TableCell>{record.driver}</TableCell>
                  <TableCell>{record.company}</TableCell>
                  <TableCell>{record.cargo}</TableCell>
                  <TableCell>{record.entryWeight}</TableCell>
                  <TableCell>{record.exitWeight}</TableCell>
                  <TableCell className="font-medium">
                    {record.netWeight}
                  </TableCell>
                  <TableCell>
                    <Badge
                      variant="secondary"
                      className={cn(
                        "flex items-center w-fit space-x-1",
                        record.status === "Concluído"
                        ? "bg-green-100 text-green-700"
                        : "bg-amber-100 text-amber-700"
                      )}
                    >
                      <span>
                        {record.status}
                      </span>
                      {
                        record.status === "Concluído"
                          ? <CheckCircle className="w-4 h-4"/>
                          : <Clock className="w-4 h-4"/>
                      }
                    </Badge>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      </CardContent>
    </Card>
  );
}

"use client";

import { Table, TableHeader, TableColumn, TableBody, TableRow, TableCell } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { CaretDownIcon, CaretUpDownIcon, CaretUpIcon, CheckCircleIcon, ClockIcon } from "@phosphor-icons/react";
import { cn } from "@/lib/utils";
import { Key, useCallback } from "react";

interface WeighingRecord {
  id: number;
  date: string;
  time: string;
  plate: string;
  driver: string;
  company: string;
  cargo: string;
  entryWeight: string;
  exitWeight: string;
  netWeight: string;
  status: string;
}

interface SortConfig {
  key: string;
  direction: "asc" | "desc";
}

interface WeighingTableProps {
  records: WeighingRecord[];
  sortConfig: SortConfig;
  onSort: (key: string) => void;
}

const columns = [
  { key: "date", label: "Data/Hora", sortable: true },
  { key: "plate", label: "Caminhão", sortable: true },
  { key: "driver", label: "Motorista", sortable: true },
  { key: "company", label: "Empresa", sortable: true },
  { key: "cargo", label: "Carga", sortable: true },
  { key: "entryWeight", label: "Entrada (kg)", sortable: true },
  { key: "exitWeight", label: "Saída (kg)", sortable: true },
  { key: "netWeight", label: "Líquido (kg)", sortable: true },
  { key: "status", label: "Status", sortable: true },
];

export default function Component({ records, sortConfig, onSort }: WeighingTableProps) {
  const getSortIcon = (columnKey: string) => {
    if (sortConfig.key !== columnKey) {
      return <CaretUpDownIcon className="h-4 w-4 text-gray-400" />;
    }
    return sortConfig.direction === "asc"
      ? <CaretUpIcon className="h-4 w-4 text-primary-600" />
      : <CaretDownIcon className="h-4 w-4 text-primary-600" />;
  };

  const renderCell = useCallback((record: WeighingRecord, columnKey: Key) => {
    const cellValue = record[columnKey as keyof WeighingRecord];

    switch (columnKey) {
      case "date":
        return (
          <div className="text-sm">
            <div className="font-medium">{record.date}</div>
            <div className="text-gray-500">{record.time}</div>
          </div>
        );
      case "plate":
        return <span className="font-medium">{record.plate}</span>;
      case "netWeight":
        return <span className="font-medium">{record.netWeight}</span>;
      case "status":
        return (
          <Badge
            variant="secondary"
            className={cn(
              "flex items-center w-fit space-x-1",
              record.status === "Concluído"
                ? "bg-green-100 text-green-700"
                : "bg-amber-100 text-amber-700"
            )}
          >
            <span>{record.status}</span>
            {record.status === "Concluído" ? (
              <CheckCircleIcon className="w-4 h-4" />
            ) : (
              <ClockIcon className="w-4 h-4" />
            )}
          </Badge>
        );
      default:
        return cellValue;
    }
  }, []);

  return (
    <div className="border-t border-gray-200">
      <Table
        isHeaderSticky
        isStriped
        aria-label="Tabela de Pesagens Recentes"
        selectionMode="none"
        classNames={{
          base: "max-h-[520px] overflow-auto",
          wrapper: "rounded-2xl border",
        }}
      >
        <TableHeader>
          {columns.map((column) => (
            <TableColumn
              key={column.key}
              className="cursor-pointer hover:bg-gray-50 select-none"
              onClick={() => column.sortable && onSort(column.key)}
              allowsSorting={column.sortable}
            >
              <div className="flex items-center justify-between">
                {column.label}
                {column.sortable && getSortIcon(column.key)}
              </div>
            </TableColumn>
          ))}
        </TableHeader>
        <TableBody emptyContent="Nenhum registro encontrado">
          {records.map((record) => (
            <TableRow key={record.id}>
              {columns.map((column) => (
                <TableCell key={column.key}>
                  {renderCell(record, column.key)}
                </TableCell>
              ))}
            </TableRow>
        </TableBody>
      </Table>
    </div>
  );
}

