interface SidebarProps {
  open: boolean;
  setOpen: (open: boolean) => void;
}

export function Sidebar({ open, setOpen }: SidebarProps) {
  const pathname = usePathname();

  const selectedLinkIndex = navigation.findIndex((item) => item.href === pathname);

  useEffect(() => {
    console.log("teste")
  }, [])

  const NavItem = ({item} : {item: NavItem}) => {
    return (
      <Link
        href={item.href}
        className={cn(
          "flex items-center gap-x-3 py-3.5 px-6",
          "text-sm leading-6 text-gray-700",
          "transition-colors group",
          "hover:text-black hover:bg-gray-100",
          pathname === item.href && "text-primary-600"
        )}
      >
        <item.icon
          className={cn(
            pathname === item.href
              ? "text-primary-600"
              : "text-gray-400 group-hover:text-black",
            "h-5 w-5 shrink-0 transition-colors"
          )}
          aria-hidden="true"
        />
        {item.name}
      </Link>
    )
  }

  return (
    <div className="flex flex-col w-[230px] z-20 fixed min-h-screen gap-y-7 bg-white shadow-lg">
      <div className="flex items-center gap-3 px-6 pt-4 shrink-0">
        <Image
          src="/images/logo.png"
          alt="Logo"
          width={74}
          height={74}
          className="h-10 w-auto"
        />
        <div className="flex flex-col">
          <h1 className="text-xl font-bold text-gray-900">Semensol</h1>
          <p className="text-xs text-gray-500 font-medium">Sistema de pesagem</p>
        </div>
      </div>
      <nav className="flex flex-col h-full gap-y-7 relative">
        <div
          style={{
            top: selectedLinkIndex === 0 ? 32 : (52 * selectedLinkIndex) + 32
          }}
          className="
            absolute bg-primary-100 w-full h-[52px]
            border-l-4 border-primary-500
            transition-all duration-300
            z-0
          "
        />
        <div>
          <div className="text-xs font-semibold leading-6 text-gray-400 uppercase tracking-wider px-6">
            OPERAÇÕES
          </div>
          <div className="mt-2">
            {navigation.map((item) => <NavItem key={item.name} item={item} />)}
          </div>
        </div>
        <div className="mt-auto">
          <div className="text-xs font-semibold leading-6 text-gray-400 uppercase tracking-wider px-6">
            SISTEMA
          </div>
          <div className="mt-2">
            {systemNavigation.map((item) => <NavItem key={item.name} item={item} />)}
          </div>
        </div>
      </nav>
      <div className="flex flex-col mt-auto">
        <div className="text-xs font-semibold leading-6 text-gray-400 uppercase tracking-wider px-6">
          STATUS
        </div>
