    ref={ref}
    className={cn(className)}
    {...props}
  />
));

const TableCell = React.forwardRef<
  React.ElementRef<typeof HeroTableCell>,
  React.ComponentPropsWithoutRef<typeof HeroTableCell>
>(({ className, ...props }, ref) => (
  <HeroTableCell
    ref={ref}
    className={cn(className)}
    {...props}
  />
));

Table.displayName = "Table";
TableHeader.displayName = "TableHeader";
TableColumn.displayName = "TableColumn";
TableBody.displayName = "TableBody";
TableRow.displayName = "TableRow";
TableCell.displayName = "TableCell";

export {
  Table,
  TableHeader,
  TableColumn,
  TableBody,
  TableRow,
  TableCell,
};

import { useState, useMemo } from "react";

export interface WeighingRecord {
  id: number;
  date: string;
  time: string;
  plate: string;
  driver: string;
  company: string;
  cargo: string;
  entryWeight: string;
  exitWeight: string;
  netWeight: string;
  status: string;
}

export interface FilterState {
  startDate: string;
  endDate: string;
  driver: string;
  company: string;
  cargoType: string;
  plate: string;
}

export interface SortConfig {
  key: string;
  direction: "asc" | "desc";
}

const mockWeighingRecords: WeighingRecord[] = [
  {
    id: 1,
