"use client";

import "./globals.css";
import {Outfit as Font} from "next/font/google";
import { ThemeProvider } from "next-themes";
import {Toaster} from "@/components/ui/toaster";
import {cn} from "@/lib/utils";
import {Navbar} from "@/components/layout/navbar";
import {NavItem} from "@/types/nav-item";
import {
  ClockIcon,
  HandshakeIcon,
  HouseIcon,
  IdentificationCardIcon,
  GearIcon,
  TruckIcon,
} from "@phosphor-icons/react";
import WeightIcon from "@/components/icons/WeightIcon";
import { IconContext } from "@phosphor-icons/react";
import ptBR from 'antd/locale/pt_BR';
import {ConfigProvider} from "antd";

const font = Font({
  weight: [
    "100",
    "200",
    "300",
    "400",
    "500",
    "600",
    "700",
    "800",
    "900"
  ],
  variable: "--font-sans",
  subsets: ["latin"],
  display: "swap",
});

const navigation: NavItem[] = [
  {name: "Tela Inicial", href: "/", section: "Operações", icon: HouseIcon},
  {name: "Pesagem Ativa", href: "/weighing", section: "Operações", icon: WeightIcon},
  {name: "Histórico", href: "/history", section: "Operações", icon: ClockIcon},
  {name: "Frota", href: "/fleet", section: "Operações", icon: TruckIcon},
  {name: "Motoristas", href: "/drivers", section: "Operações", icon: IdentificationCardIcon},
];

export default function RootLayout({children}: {
  children: React.ReactNode;
}) {

  return (
    <html lang="pt-BR" suppressHydrationWarning>
      <head>
        <meta name="mobile-web-app-status-bar-style" content="black-translucent"/>
        <meta name="mobile-web-app-capable" content="yes"/>
        <meta name="apple-mobile-web-app-capable" content="yes"/>
        <meta name="apple-mobile-web-app-status-bar-style" content="default"/>
        <meta name="apple-mobile-web-app-title" content="Semensol Agro"/>
        <meta name="viewport" content="width=device-width, initial-scale=1"/>
        <meta name="theme-color" content="#059669"/>
        <link rel="icon" href="/images/favicon.ico"/>
        <link rel="apple-touch-icon" href="/images/logo.png"/>
        <link rel="manifest" href="/manifest.json"/>
        <title>Semensol Agro</title>
      </head>
      <body
        className={cn(
          font.variable,
          "flex flex-col min-h-screen max-h-screen waves-background relative"
        )}
      >
        <ThemeProvider
          attribute="class"
          defaultTheme="light"
          enableSystem={false}
          disableTransitionOnChange
        >
          <ConfigProvider locale={ptBR}>
            <IconContext.Provider
              value={{
                size: 32,
                weight: "bold",
                mirrored: false,
                "aria-hidden": true,
              }}
            >
              {children}
              <Navbar navigation={navigation}/>
              <Toaster/>
            </IconContext.Provider>
          </ConfigProvider>
        </ThemeProvider>
      </body>
    </html>
  );
}

"use client";

import "./globals.css";
import {Outfit as Font} from "next/font/google";
import { ThemeProvider } from "next-themes";
import {Toaster} from "@/components/ui/toaster";
import {cn} from "@/lib/utils";
import {Navbar} from "@/components/layout/navbar";
import {NavItem} from "@/types/nav-item";
import {
  ClockIcon,
  HandshakeIcon,
  HouseIcon,
  IdentificationCardIcon,
  GearIcon,
  TruckIcon,
} from "@phosphor-icons/react";
import WeightIcon from "@/components/icons/WeightIcon";
import { IconContext } from "@phosphor-icons/react";
import ptBR from 'antd/locale/pt_BR';
import {ConfigProvider} from "antd";
import {PWAInstaller} from "@/components/ui/pwa-installer";

const font = Font({
  weight: [
    "100",
    "200",
    "300",
    "400",
    "500",
    "600",
    "700",
    "800",
    "900"
  ],
  variable: "--font-sans",
  subsets: ["latin"],
  display: "swap",
});

const navigation: NavItem[] = [
  {name: "Tela Inicial", href: "/", section: "Operações", icon: HouseIcon},
  {name: "Pesagem Ativa", href: "/weighing", section: "Operações", icon: WeightIcon},
  {name: "Histórico", href: "/history", section: "Operações", icon: ClockIcon},
  {name: "Frota", href: "/fleet", section: "Operações", icon: TruckIcon},
  {name: "Motoristas", href: "/drivers", section: "Operações", icon: IdentificationCardIcon},
];

export default function RootLayout({children}: {
  children: React.ReactNode;
}) {

  return (
    <html lang="pt-BR" suppressHydrationWarning>
      <head>
        <meta name="mobile-web-app-status-bar-style" content="black-translucent"/>
        <meta name="mobile-web-app-capable" content="yes"/>
        <meta name="apple-mobile-web-app-capable" content="yes"/>
        <meta name="apple-mobile-web-app-status-bar-style" content="default"/>
        <meta name="apple-mobile-web-app-title" content="Semensol Agro"/>
        <meta name="viewport" content="width=device-width, initial-scale=1"/>
        <meta name="theme-color" content="#059669"/>
        <link rel="icon" href="/images/favicon.ico"/>
        <link rel="apple-touch-icon" href="/images/logo.png"/>
        <link rel="manifest" href="/manifest.json"/>
        <title>Semensol Agro</title>
      </head>
      <body
        className={cn(
          font.variable,
          "flex flex-col min-h-screen max-h-screen waves-background relative"
        )}
      >
        <ThemeProvider
          attribute="class"
          defaultTheme="light"
          enableSystem={false}
          disableTransitionOnChange
        >
          <ConfigProvider locale={ptBR}>
            <IconContext.Provider
              value={{
                size: 32,
                weight: "bold",
                mirrored: false,
                "aria-hidden": true,
              }}
            >
              {children}
              <Navbar navigation={navigation}/>
              <Toaster/>
              <PWAInstaller/>
            </IconContext.Provider>
          </ConfigProvider>
        </ThemeProvider>
      </body>
    </html>
  );
}

"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { BuildingOfficeIcon, PlusIcon, PencilSimpleLineIcon, PhoneIcon, EnvelopeSimpleIcon, MapPinIcon } from "@phosphor-icons/react";
import { FAB } from "@/components/ui/fab";
import { AddPartnerModal } from "@/components/partners/add-partner-modal";
import { EditPartnerModal } from "@/components/partners/edit-partner-modal";
import { useState } from "react";

interface Partner {
  id: number;
  name: string;
  cnpj: string;
  phone: string;
  email: string;
  address: string;
  city: string;
  status: "Ativa" | "Inativa";
  type: string;
}

export default function PartnerPage() {
  const [addModalOpen, setAddModalOpen] = useState(false);
  const [editModalOpen, setEditModalOpen] = useState(false);
  const [selectedPartner, setSelectedPartner] = useState<Partner | null>(null);
  const [partners, setPartners] = useState<Partner[]>([
    {
      id: 1,
      name: "Agro Brasil Ltda",
      cnpj: "12.345.678/0001-90",
      phone: "(11) 3333-3333",
      email: "contato@agrobrasil.com",
      address: "Rua das Plantações, 123",
      city: "Ribeirão Preto - SP 14000-000",
      status: "Ativa",
      type: "Cliente preferencial",
    },
    {
      id: 2,
      name: "Transportes Campo",
      cnpj: "98.765.432/0001-12",
      phone: "(16) 2222-2222",
      email: "admin@transportescampo.com",
      address: "Av. Rural, 456",
      city: "Sertãozinho - SP 14160-000",
      status: "Ativa",
      type: "Transportadora",
    },
  ]);

  const handleNewPartner = () => {
    setAddModalOpen(true);
  };

  const handleEditPartner = (partner: Partner) => {
    setSelectedPartner(partner);
    setEditModalOpen(true);
  };

  const handleSavePartner = (newPartner: Omit<Partner, "id">) => {
    const partner: Partner = {
      ...newPartner,
      id: Math.max(...partners.map((p) => p.id)) + 1,
    };
    setPartners((prev) => [...prev, partner]);
  };

  const handleUpdatePartner = (updatedPartner: Partner) => {
    setPartners((prev) =>
      prev.map((partner) =>
        partner.id === updatedPartner.id ? updatedPartner : partner
      )
    );
  };

  const handleDeletePartner = (partnerId: number) => {
    setPartners((prev) => prev.filter((partner) => partner.id !== partnerId));
  };

  return (
    <main>
      <div>
        <div>
          <h1 className="text-3xl font-bold tracking-tight text-white">
            Empresas parceiras
          </h1>
          <p className="text-gray-200 mt-1">Gerencie as empresas parceiras</p>
        </div>
      </div>

      <div className="grid grid-cols-1 gap-6 md:grid-cols-2 xl:grid-cols-3">
        {partners.map((partner) => (
          <Card key={partner.id} className="relative">
            <CardHeader className="pb-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <div className="h-12 w-12 rounded-2xl bg-primary-100 flex items-center justify-center">
                    <BuildingOfficeIcon className="h-6 w-6 text-primary-600" />
                  </div>
                  <div>
                    <h3 className="text-lg font-bold">
                      {partner.name}
                    </h3>
                    <p className="text-sm text-gray-400">CNPJ: {partner.cnpj}</p>
                  </div>
                </div>
                <Badge
                  className={
                    partner.status === "Ativa"
                      ? "bg-green-100 text-green-700 border-green-200"
                      : "bg-red-100 text-red-700 border-red-200"
                  }
                >
                  {partner.status}
                </Badge>
              </div>
            </CardHeader>
            <CardContent className="flex flex-col">
              <div className="space-y-3">
                <div className="flex items-center gap-2">
                  <PhoneIcon className="h-4 w-4 text-primary-600" />
                  <p className="text-sm font-medium text-card-foreground">{partner.phone}</p>
                </div>
                <div className="flex items-center gap-2">
                  <EnvelopeSimpleIcon className="h-4 w-4 text-primary-600" />
                  <p className="text-sm font-medium text-card-foreground">{partner.email}</p>
                </div>
                <div className="flex items-start gap-2">
                  <MapPinIcon className="h-4 w-4 text-primary-600 mt-0.5" />
                  <div>
                    <p className="text-sm font-medium text-card-foreground">{partner.address}</p>
                    <p className="text-sm text-gray-600">{partner.city}</p>
                  </div>
                </div>
                <div>
                  <p className="text-sm font-semibold text-primary-600">Tipo</p>
                  <p className="text-sm font-medium text-card-foreground">{partner.type}</p>
                </div>
              </div>
              <Button
                variant="secondary"
                size="sm"
                className="ml-auto mt-6"
                onClick={() => handleEditPartner(partner)}
              >
                <PencilSimpleLineIcon className="h-4 w-4 mr-2" />
                Editar
              </Button>
            </CardContent>
          </Card>
        ))}
      </div>

      <FAB
        icon={PlusIcon}
        label="Nova Empresa"
        onClick={handleNewPartner}
      />

      <AddPartnerModal
        open={addModalOpen}
        onOpenChange={setAddModalOpen}
        onSave={handleSavePartner}
      />

      <EditPartnerModal
        open={editModalOpen}
        onOpenChange={setEditModalOpen}
        partner={selectedPartner}
        onSave={handleUpdatePartner}
        onDelete={handleDeletePartner}
      />
    </main>
  );
}

"use client";

import {useState, useEffect} from "react";
import {Card, CardContent, CardHeader, CardTitle} from "@/components/ui/card";
import {Button} from "@/components/ui/button";
import {Input} from "@/components/ui/input";
import {Label} from "@/components/ui/label";
import {Select, SelectContent, SelectItem, SelectTrigger, SelectValue,} from "@/components/ui/select";
import {CheckCircleIcon, ClockIcon, PlayIcon, PlugsConnectedIcon, ScalesIcon, SquareIcon, PlugsIcon} from "@phosphor-icons/react";
import WeightIcon from "@/components/icons/WeightIcon";
import {cn} from "@/lib/utils";
import {Badge} from "@/components/ui/badge";
import {apiClient} from "@/lib/api";
import {VideoVerification} from "@/components/weighing/video-verification";
import {useToast} from "@/hooks/use-toast";

interface Motorista {
  id: number;
  nome: string;
}

interface CicloAberto {
  id_pesagem: number;
  placa: string;
  motorista: string;
}

export default function WeighingPage() {
  const [isConnected, setIsConnected] = useState(false);
  const [currentWeight, setCurrentWeight] = useState(0);
  const [isWeighing, setIsWeighing] = useState(false);
  const [motoristas, setMotoristas] = useState<Motorista[]>([]);
  const [ciclosAbertos, setCiclosAbertos] = useState<CicloAberto[]>([]);
  const [loading, setLoading] = useState(false);
  const [initialLoading, setInitialLoading] = useState(true);
  const [formData, setFormData] = useState({
    placa: "",
    motorista_id: "",
    peso: ""
  });
  const [verificationComplete, setVerificationComplete] = useState(false);
  const { toast } = useToast();

  useEffect(() => {
    loadMotoristas();
    loadCiclosAbertos();
  }, []);

  const loadMotoristas = async () => {
    const response = await apiClient.getMotoristas();
    if (response.error) {
      toast({
        title: "Erro ao carregar motoristas",
        description: response.error,
        variant: "destructive",
      });
    } else if (response.data) {
      setMotoristas(response.data);
    }
  };

  const loadCiclosAbertos = async () => {
    const response = await apiClient.getCiclosAbertos();
    if (response.error) {
      toast({
        title: "Erro ao carregar ciclos",
        description: response.error,
        variant: "destructive",
      });
    } else if (response.data) {
      setCiclosAbertos(response.data);
    }
  };

  const handleDriverVerified = (driverId: number, driverName: string) => {
    setFormData(prev => ({ ...prev, motorista_id: driverId.toString() }));
  };

  const handlePlateDetected = (plate: string) => {
    setFormData(prev => ({ ...prev, placa: plate }));
  };

  const handleVerificationComplete = (success: boolean) => {
    setVerificationComplete(success);
  };

  const handleRegistrarEntrada = async () => {
    if (!formData.placa || !formData.motorista_id || !currentWeight) return;

    setLoading(true);
    const response = await apiClient.registrarEntrada({
      placa: formData.placa,
      motorista_id: parseInt(formData.motorista_id),
      peso: currentWeight
    });

    if (response.error) {
      toast({
        title: "Erro ao registrar entrada",
        description: response.error,
        variant: "destructive",
      });
    } else if (response.data) {
      toast({
        title: "Entrada registrada",
        description: "Entrada registrada com sucesso!",
      });
      setFormData({ placa: "", motorista_id: "", peso: "" });
      setCurrentWeight(0);
      setVerificationComplete(false);
      loadCiclosAbertos();
    }
    setLoading(false);
  };

  const handleRegistrarSaida = async (eventoId: number) => {
    if (!currentWeight) return;

    setLoading(true);
    const response = await apiClient.registrarSaida({
      evento_id: eventoId,
      peso: currentWeight
    });

    if (response.error) {
      toast({
        title: "Erro ao registrar saída",
        description: response.error,
        variant: "destructive",
      });
    } else {
      toast({
        title: "Saída registrada",
        description: "Saída registrada com sucesso!",
      });
      setCurrentWeight(0);
      loadCiclosAbertos();
    }
    setLoading(false);
  };

  return (
    <main className="p-4 md:p-6">
      <div className="flex flex-col gap-2 mb-6">
        <h1 className="text-3xl font-bold text-white">
          Central de Pesagem
        </h1>
        <p className="text-gray-200">
          Controle e monitoramento das operações de pesagem em tempo real
        </p>
      </div>

      <div className="grid grid-cols-1 xl:grid-cols-2 gap-6">
        <VideoVerification
          onDriverVerified={handleDriverVerified}
          onPlateDetected={handlePlateDetected}
          onVerificationComplete={handleVerificationComplete}
          formData={formData}
          setFormData={setFormData}
          motoristas={motoristas}
          verificationComplete={verificationComplete}
          ciclosAbertos={ciclosAbertos}
          isConnected={isConnected}
          loading={loading}
          onRegistrarSaida={handleRegistrarSaida}
        />

        <Card className="w-full max-w-none">
          <div className="grid grid-cols-1 lg:grid-cols-2 min-h-[600px]">
            <div className="border-b lg:border-b-0 lg:border-r border-dashed">
              <CardHeader>
                <CardTitle className="text-lg font-semibold flex items-center gap-2">
                  <ScalesIcon className="h-5 w-5"/>
                  Controle da Balança
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Verification status and cycles moved to VideoVerification component */}
              </CardContent>
            </div>

            <div className="flex flex-col">
              <CardHeader className="flex-row justify-between items-center">
                <CardTitle className="text-lg font-semibold flex items-center gap-2">
                  <WeightIcon className="h-5 w-5"/>
                  Balança
                </CardTitle>
                <Badge
                  variant="secondary"
                  className={cn(
                    "flex items-center text-xs gap-1",
                    isConnected
                      ? "bg-green-100 text-green-700"
                      : "bg-red-100 text-red-700"
                  )}
                >
                  <div
                    className={cn(
                      "w-1 h-1 rounded-full",
                      isConnected ? "bg-green-700" : "bg-red-700"
                    )}
                  />
                  {isConnected ? "Conectada" : "Desconectada"}
                </Badge>
              </CardHeader>
              <CardContent className="flex flex-col h-full justify-between">
                <div className="text-center my-auto">
                  {!isConnected ? (
                    <>
                      <WeightIcon className="h-16 w-16 text-gray-300 mx-auto mb-4"/>
                      <p className="text-gray-500">Balança desconectada</p>
                    </>
                  ) : (
                    <div className="space-y-4">
                      <p className="text-4xl md:text-6xl lg:text-7xl font-bold">{currentWeight} kg</p>
                      <Input
                        type="number"
                        placeholder="Peso manual"
                        value={currentWeight}
                        onChange={(e) => setCurrentWeight(Number(e.target.value))}
                        className="text-center text-lg"
                      />
                    </div>
                  )}
                </div>
                <div className="flex flex-col gap-3 mt-6">
                  <Button
                    variant="secondary"
                    className="flex-1"
                    onClick={() => setCurrentWeight(0)}
                  >
                    Zerar Peso
                  </Button>
                  <Button
                    onClick={() => setIsConnected(!isConnected)}
                    variant={isConnected ? "destructive" : "default"}
                    className={cn(
                      !isConnected && "bg-green-700 hover:bg-green-700/90",
                      "flex-1 flex items-center gap-2"
                    )}
                  >
                    {isConnected ? <PlugsIcon className="h-4 w-4"/> : <PlugsConnectedIcon className="h-4 w-4"/>}
                    {isConnected ? "Desconectar" : "Conectar Balança"}
                  </Button>
                  <Button
                    className="w-full"
                    disabled={!isConnected || !formData.placa || !formData.motorista_id || loading}
                    onClick={handleRegistrarEntrada}
                  >
                    {loading ? (
                      <ClockIcon className="h-4 w-4 mr-2"/>
                    ) : isWeighing ? (
                      <SquareIcon className="h-4 w-4 mr-2"/>
                    ) : (
                      <PlayIcon className="h-4 w-4 mr-2"/>
                    )}
                    {loading ? "Processando..." : "Registrar Entrada"}
                  </Button>
                </div>
              </CardContent>
            </div>
          </div>
        </Card>
      </div>
    </main>
  );
}

