"use client";

import {useState, useRef, useCallback} from "react";
import {Button} from "@/components/ui/button";
import {Card, CardContent, CardHeader, CardTitle} from "@/components/ui/card";
import {Input} from "@/components/ui/input";
import {Label} from "@/components/ui/label";
import {Select, SelectContent, SelectItem, SelectTrigger, SelectValue} from "@/components/ui/select";
import {Badge} from "@/components/ui/badge";
import {CameraIcon, UserCheck, Car} from "@phosphor-icons/react";
import {apiClient} from "@/lib/api";
import {cn} from "@/lib/utils";
import {useToast} from "@/hooks/use-toast";

interface Motorista {
  id: number;
  nome: string;
}

interface VideoVerificationProps {
  onDriverVerified: (driverId: number, driverName: string) => void;
  onPlateDetected: (plate: string) => void;
  onVerificationComplete: (success: boolean) => void;
  formData: {
    placa: string;
    motorista_id: string;
  };
  setFormData: (data: any) => void;
  motoristas: Motorista[];
}

export function VideoVerification({
  onDriverVerified,
  onPlateDetected,
  onVerificationComplete,
  formData,
  setFormData,
  motoristas
}: VideoVerificationProps) {
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [isStreaming, setIsStreaming] = useState(false);
  const [verificationState, setVerificationState] = useState<{
    driver: { verified: boolean; name?: string; confidence?: number };
    plate: { detected: boolean; value?: string };
    loading: boolean;
  }>({
    driver: { verified: false },
    plate: { detected: false },
    loading: false
  });
  const { toast } = useToast();

  const startCamera = useCallback(async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          width: { ideal: 640 },
          height: { ideal: 480 },
          facingMode: 'user'
        }
      });

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setIsStreaming(true);
      }
    } catch (error) {
      console.error('Erro ao acessar câmera:', error);
      toast({
        title: "Erro de câmera",
        description: "Não foi possível acessar a câmera. Verifique as permissões.",
        variant: "destructive",
      });
    }
  }, [toast]);

  const stopCamera = useCallback(() => {
    if (videoRef.current?.srcObject) {
      const tracks = (videoRef.current.srcObject as MediaStream).getTracks();
      tracks.forEach(track => track.stop());
      videoRef.current.srcObject = null;
      setIsStreaming(false);
    }
  }, []);

  const captureImage = useCallback((): Promise<Blob | null> => {
    if (!videoRef.current || !canvasRef.current) return Promise.resolve(null);

    const canvas = canvasRef.current;
    const video = videoRef.current;
    const context = canvas.getContext('2d');

    if (!context) return Promise.resolve(null);

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    // Flip the image horizontally before sending to API
    context.translate(canvas.width, 0);
    context.scale(-1, 1);
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    return new Promise((resolve) => {
      canvas.toBlob((blob) => {
        resolve(blob);
      }, 'image/jpeg', 0.8);
    });
  }, []);

  const verifyDriver = useCallback(async () => {
    const imageBlob = await captureImage();
    if (!imageBlob) {
      toast({
        title: "Erro na captura",
        description: "Não foi possível capturar a imagem da câmera.",
        variant: "destructive",
      });
      return;
    }

    setVerificationState(prev => ({ ...prev, loading: true }));

    const formData = new FormData();
    formData.append('imagem_rosto', imageBlob, 'driver.jpg');

    const response = await apiClient.reconhecerMotorista(formData);

    if (response.error) {
      toast({
        title: "Erro na verificação",
        description: response.error,
        variant: "destructive",
      });
      setVerificationState(prev => ({ ...prev, driver: { verified: false }, loading: false }));
    } else if (response.data?.motorista_reconhecido) {
      const driverData = {
        verified: true,
        name: response.data.motorista_nome,
        confidence: response.data.confianca
      };

      setVerificationState(prev => ({ ...prev, driver: driverData, loading: false }));

      if (response.data.motorista_id && response.data.motorista_nome) {
        onDriverVerified(response.data.motorista_id, response.data.motorista_nome);
        toast({
          title: "Motorista verificado",
          description: `${response.data.motorista_nome} identificado com sucesso!`,
        });
      }
    } else {
      toast({
        title: "Motorista não reconhecido",
        description: "Não foi possível identificar o motorista na imagem.",
        variant: "destructive",
      });
      setVerificationState(prev => ({ ...prev, driver: { verified: false }, loading: false }));
    }
  }, [captureImage, onDriverVerified, toast]);

  const detectPlate = useCallback(async () => {
    const imageBlob = await captureImage();
    if (!imageBlob) {
      toast({
        title: "Erro na captura",
        description: "Não foi possível capturar a imagem da câmera.",
        variant: "destructive",
      });
      return;
    }

    setVerificationState(prev => ({ ...prev, loading: true }));

    const formData = new FormData();
    formData.append('imagem_placa', imageBlob, 'plate.jpg');

    const response = await apiClient.reconhecerPlaca(formData);

    if (response.error) {
      toast({
        title: "Erro na detecção de placa",
        description: response.error,
        variant: "destructive",
      });
      setVerificationState(prev => ({ ...prev, plate: { detected: false }, loading: false }));
    } else if (response.data?.placa) {
      const plateData = {
        detected: true,
        value: response.data.placa
      };

      setVerificationState(prev => ({ ...prev, plate: plateData, loading: false }));
      onPlateDetected(response.data.placa);
      toast({
        title: "Placa detectada",
        description: `Placa ${response.data.placa} identificada com sucesso!`,
      });
    } else {
      toast({
        title: "Placa não detectada",
        description: "Não foi possível identificar a placa na imagem.",
        variant: "destructive",
      });
      setVerificationState(prev => ({ ...prev, plate: { detected: false }, loading: false }));
    }
  }, [captureImage, onPlateDetected, toast]);

  const performCompleteVerification = useCallback(async () => {
    const imageBlob = await captureImage();
    if (!imageBlob) {
      toast({
        title: "Erro na captura",
        description: "Não foi possível capturar a imagem da câmera.",
        variant: "destructive",
      });
      return;
    }

    setVerificationState(prev => ({ ...prev, loading: true }));

    const formData = new FormData();
    formData.append('imagem_rosto', imageBlob, 'driver.jpg');
    formData.append('imagem_placa', imageBlob, 'plate.jpg');

    const response = await apiClient.reconhecimentoCompleto(formData);

    if (response.error) {
      toast({
        title: "Erro na verificação completa",
        description: response.error,
        variant: "destructive",
      });
      setVerificationState(prev => ({ ...prev, loading: false }));
    } else if (response.data) {
      const newState = {
        driver: {
          verified: response.data.motorista_reconhecido,
          name: response.data.motorista_nome,
          confidence: response.data.confianca_motorista
        },
        plate: {
          detected: response.data.placa_valida,
          value: response.data.placa_reconhecida
        },
        loading: false
      };

      setVerificationState(newState);

      if (response.data.motorista_reconhecido && response.data.motorista_id && response.data.motorista_nome) {
        onDriverVerified(response.data.motorista_id, response.data.motorista_nome);
      }

      if (response.data.placa_reconhecida) {
        onPlateDetected(response.data.placa_reconhecida);
      }

      const success = response.data.motorista_reconhecido && response.data.placa_valida;
      onVerificationComplete(success);

      if (success) {
        toast({
          title: "Verificação completa",
          description: "Motorista e placa verificados com sucesso!",
        });
      } else {
        toast({
          title: "Verificação parcial",
          description: "Nem todos os elementos foram verificados com sucesso.",
          variant: "destructive",
        });
      }
    }
  }, [captureImage, onDriverVerified, onPlateDetected, onVerificationComplete, toast]);

  return (
    <Card className="w-full max-w-none">
      <CardHeader>
        <CardTitle className="text-lg font-semibold flex items-center gap-2">
          <CameraIcon className="h-5 w-5"/>
          Verificação e Entrada
        </CardTitle>
      </CardHeader>
      <CardContent className="flex flex-col xl:flex-row gap-6">
        {/* Manual Input Section */}
        <div className="flex flex-col w-full h-full space-y-4">
          <h3 className="text-md font-medium flex items-center gap-2">
            <UserCheck className="h-4 w-4"/>
            Entrada Manual
          </h3>

          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
            <div className="sm:col-span-2">
              <Label htmlFor="placa">Placa do Veículo</Label>
              <Input
                id="placa"
                placeholder="ABC-1234"
                value={formData.placa}
                onChange={(e) => setFormData(prev => ({ ...prev, placa: e.target.value.toUpperCase() }))}
                className={cn(
                  formData.placa && "border-green-500 bg-green-50"
                )}
              />
            </div>
            <div className="sm:col-span-2">
              <Label htmlFor="driver">Motorista</Label>
              <Select value={formData.motorista_id} onValueChange={(value) => setFormData(prev => ({ ...prev, motorista_id: value }))}>
                <SelectTrigger className={cn(
                  formData.motorista_id && "border-green-500 bg-green-50"
                )}>
                  <SelectValue placeholder="Selecione o motorista"/>
                </SelectTrigger>
                <SelectContent>
                  {motoristas.map((motorista) => (
                    <SelectItem key={motorista.id} value={motorista.id.toString()}>
                      {motorista.nome}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>

        {/* Video Verification Section */}
        <div className="flex flex-col w-full h-full space-y-4">
          <h3 className="text-md font-medium flex items-center gap-2">
            <CameraIcon className="h-4 w-4"/>
            Entrada por Vídeo
          </h3>

          <div className="relative">
            <video
              ref={videoRef}
              autoPlay
              playsInline
              muted
              className="w-full aspect-video bg-gray-100 rounded-lg object-cover"
              style={{ transform: 'scaleX(-1)' }}
            />
            <canvas ref={canvasRef} className="hidden" />

            {!isStreaming && (
              <div className="absolute inset-0 flex items-center justify-center bg-gray-100 rounded-lg">
                <div className="text-center">
                  <CameraIcon className="h-12 w-12 text-gray-400 mx-auto mb-2"/>
                  <p className="text-gray-500">Câmera desligada</p>
                  <p className="text-xs text-gray-400">Clique em <strong>Iniciar Câmera</strong> para começar</p>
                </div>
              </div>
            )}
          </div>

          {/* Camera Controls */}
          <div className="grid grid-cols-1 gap-2">
            <Button
              onClick={isStreaming ? stopCamera : startCamera}
              variant={isStreaming ? "destructive" : "default"}
              className="flex items-center gap-2"
              disabled={verificationState.loading}
            >
              <CameraIcon className="h-4 w-4"/>
              {isStreaming ? "Parar Câmera" : "Iniciar Câmera"}
            </Button>
          </div>

          {/* Individual Verification Buttons */}
          <div className="grid grid-cols-2 gap-2">
            <Button
              onClick={verifyDriver}
              disabled={!isStreaming || verificationState.loading}
              variant="outline"
              size="sm"
              className="flex items-center gap-2"
            >
              <UserCheck className="h-3 w-3"/>
              Verificar Motorista
            </Button>

            <Button
              onClick={detectPlate}
              disabled={!isStreaming || verificationState.loading}
              variant="outline"
              size="sm"
              className="flex items-center gap-2"
            >
              <Car className="h-3 w-3"/>
              Detectar Placa
            </Button>
          </div>
        </div>

        {/* Verification Status */}
        {(verificationState.driver.verified || verificationState.plate.detected) && (
          <div className="space-y-2">
            <h4 className="text-sm font-medium">Status da Verificação</h4>
            <div className="space-y-2">
              {verificationState.driver.verified && (
                <div className="flex items-center gap-2 p-2 bg-green-50 border border-green-200 rounded">
                  <UserCheck className="h-4 w-4 text-green-600"/>
                  <span className="text-sm text-green-800">
                    Motorista: {verificationState.driver.name}
                    {verificationState.driver.confidence && (
                      <span className="text-xs ml-1">({Math.round(verificationState.driver.confidence * 100)}%)</span>
                    )}
                  </span>
                </div>
              )}

              {verificationState.plate.detected && (
                <div className="flex items-center gap-2 p-2 bg-green-50 border border-green-200 rounded">
                  <Car className="h-4 w-4 text-green-600"/>
                  <span className="text-sm text-green-800">
                    Placa: {verificationState.plate.value}
                  </span>
                </div>
              )}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

"use client";

import {useState, useRef, useCallback} from "react";
import {Button} from "@/components/ui/button";
import {Card, CardContent, CardHeader, CardTitle} from "@/components/ui/card";
import {Input} from "@/components/ui/input";
import {Label} from "@/components/ui/label";
import {Select, SelectContent, SelectItem, SelectTrigger, SelectValue} from "@/components/ui/select";
import {Badge} from "@/components/ui/badge";
import {CameraIcon, UserCheck, Car, CheckCircleIcon} from "@phosphor-icons/react";
import {apiClient} from "@/lib/api";
import {cn} from "@/lib/utils";
import {useToast} from "@/hooks/use-toast";

interface Motorista {
  id: number;
  nome: string;
}

interface VideoVerificationProps {
  onDriverVerified: (driverId: number, driverName: string) => void;
  onPlateDetected: (plate: string) => void;
  onVerificationComplete: (success: boolean) => void;
  formData: {
    placa: string;
    motorista_id: string;
  };
  setFormData: (data: any) => void;
  motoristas: Motorista[];
  verificationComplete: boolean;
  ciclosAbertos: any[];
  isConnected: boolean;
  loading: boolean;
  onRegistrarSaida: (eventoId: number) => void;
}

export function VideoVerification({
  onDriverVerified,
  onPlateDetected,
  onVerificationComplete,
  formData,
  setFormData,
  motoristas,
  verificationComplete: initialVerificationComplete,
  ciclosAbertos: initialCiclosAbertos,
  isConnected,
  loading: initialLoading,
  onRegistrarSaida
}: VideoVerificationProps) {
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [isStreaming, setIsStreaming] = useState(false);
  const [verificationState, setVerificationState] = useState<{
    driver: { verified: boolean; name?: string; confidence?: number };
    plate: { detected: boolean; value?: string };
    loading: boolean;
  }>({
    driver: { verified: false },
    plate: { detected: false },
    loading: false
  });
  const [verificationComplete, setVerificationComplete] = useState(initialVerificationComplete);
  const [ciclosAbertos, setCiclosAbertos] = useState<any[]>(initialCiclosAbertos);
  const { toast } = useToast();

  const startCamera = useCallback(async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          width: { ideal: 640 },
          height: { ideal: 480 },
          facingMode: 'user'
        }
      });

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setIsStreaming(true);
      }
    } catch (error) {
      console.error('Erro ao acessar câmera:', error);
      toast({
        title: "Erro de câmera",
        description: "Não foi possível acessar a câmera. Verifique as permissões.",
        variant: "destructive",
      });
    }
  }, [toast]);

  const stopCamera = useCallback(() => {
    if (videoRef.current?.srcObject) {
      const tracks = (videoRef.current.srcObject as MediaStream).getTracks();
      tracks.forEach(track => track.stop());
      videoRef.current.srcObject = null;
      setIsStreaming(false);
    }
  }, []);

  const captureImage = useCallback((): Promise<Blob | null> => {
    if (!videoRef.current || !canvasRef.current) return Promise.resolve(null);

    const canvas = canvasRef.current;
    const video = videoRef.current;
    const context = canvas.getContext('2d');

    if (!context) return Promise.resolve(null);

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    // Flip the image horizontally before sending to API
    context.translate(canvas.width, 0);
    context.scale(-1, 1);
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    return new Promise((resolve) => {
      canvas.toBlob((blob) => {
        resolve(blob);
      }, 'image/jpeg', 0.8);
    });
  }, []);

  const verifyDriver = useCallback(async () => {
    const imageBlob = await captureImage();
    if (!imageBlob) {
      toast({
        title: "Erro na captura",
        description: "Não foi possível capturar a imagem da câmera.",
        variant: "destructive",
      });
      return;
    }

    setVerificationState(prev => ({ ...prev, loading: true }));

    const formData = new FormData();
    formData.append('imagem_rosto', imageBlob, 'driver.jpg');

    const response = await apiClient.reconhecerMotorista(formData);

    if (response.error) {
      toast({
        title: "Erro na verificação",
        description: response.error,
        variant: "destructive",
      });
      setVerificationState(prev => ({ ...prev, driver: { verified: false }, loading: false }));
    } else if (response.data?.motorista_reconhecido) {
      const driverData = {
        verified: true,
        name: response.data.motorista_nome,
        confidence: response.data.confianca
      };

      setVerificationState(prev => ({ ...prev, driver: driverData, loading: false }));

      if (response.data.motorista_id && response.data.motorista_nome) {
        onDriverVerified(response.data.motorista_id, response.data.motorista_nome);
        toast({
          title: "Motorista verificado",
          description: `${response.data.motorista_nome} identificado com sucesso!`,
        });
      }
    } else {
      toast({
        title: "Motorista não reconhecido",
        description: "Não foi possível identificar o motorista na imagem.",
        variant: "destructive",
      });
      setVerificationState(prev => ({ ...prev, driver: { verified: false }, loading: false }));
    }
  }, [captureImage, onDriverVerified, toast]);

  const detectPlate = useCallback(async () => {
    const imageBlob = await captureImage();
    if (!imageBlob) {
      toast({
        title: "Erro na captura",
        description: "Não foi possível capturar a imagem da câmera.",
        variant: "destructive",
      });
      return;
    }

    setVerificationState(prev => ({ ...prev, loading: true }));

    const formData = new FormData();
    formData.append('imagem_placa', imageBlob, 'plate.jpg');

    const response = await apiClient.reconhecerPlaca(formData);

    if (response.error) {
      toast({
        title: "Erro na detecção de placa",
        description: response.error,
        variant: "destructive",
      });
      setVerificationState(prev => ({ ...prev, plate: { detected: false }, loading: false }));
    } else if (response.data?.placa) {
      const plateData = {
        detected: true,
        value: response.data.placa
      };

      setVerificationState(prev => ({ ...prev, plate: plateData, loading: false }));
      onPlateDetected(response.data.placa);
      toast({
        title: "Placa detectada",
        description: `Placa ${response.data.placa} identificada com sucesso!`,
      });
    } else {
      toast({
        title: "Placa não detectada",
        description: "Não foi possível identificar a placa na imagem.",
        variant: "destructive",
      });
      setVerificationState(prev => ({ ...prev, plate: { detected: false }, loading: false }));
    }
  }, [captureImage, onPlateDetected, toast]);

  const performCompleteVerification = useCallback(async () => {
    const imageBlob = await captureImage();
    if (!imageBlob) {
      toast({
        title: "Erro na captura",
        description: "Não foi possível capturar a imagem da câmera.",
        variant: "destructive",
      });
      return;
    }

    setVerificationState(prev => ({ ...prev, loading: true }));

    const formData = new FormData();
    formData.append('imagem_rosto', imageBlob, 'driver.jpg');
    formData.append('imagem_placa', imageBlob, 'plate.jpg');

    const response = await apiClient.reconhecimentoCompleto(formData);

    if (response.error) {
      toast({
        title: "Erro na verificação completa",
        description: response.error,
        variant: "destructive",
      });
      setVerificationState(prev => ({ ...prev, loading: false }));
    } else if (response.data) {
      const newState = {
        driver: {
          verified: response.data.motorista_reconhecido,
          name: response.data.motorista_nome,
          confidence: response.data.confianca_motorista
        },
        plate: {
          detected: response.data.placa_valida,
          value: response.data.placa_reconhecida
        },
        loading: false
      };

      setVerificationState(newState);

      if (response.data.motorista_reconhecido && response.data.motorista_id && response.data.motorista_nome) {
        onDriverVerified(response.data.motorista_id, response.data.motorista_nome);
      }

      if (response.data.placa_reconhecida) {
        onPlateDetected(response.data.placa_reconhecida);
      }

      const success = response.data.motorista_reconhecido && response.data.placa_valida;
      onVerificationComplete(success);

      setVerificationComplete(true);

      if (success) {
        toast({
          title: "Verificação completa",
          description: "Motorista e placa verificados com sucesso!",
        });
      } else {
        toast({
          title: "Verificação parcial",
          description: "Nem todos os elementos foram verificados com sucesso.",
          variant: "destructive",
        });
      }
    }
  }, [captureImage, onDriverVerified, onPlateDetected, onVerificationComplete, toast]);

  return (
    <Card className="w-full max-w-none">
      <CardHeader>
        <CardTitle className="text-lg font-semibold flex items-center gap-2">
          <CameraIcon className="h-5 w-5"/>
          Verificação e Entrada
        </CardTitle>
      </CardHeader>
      <CardContent className="flex flex-col xl:flex-row gap-6">
        {/* Manual Input Section */}
        <div className="flex flex-col w-full h-full space-y-4">
          <h3 className="text-md font-medium flex items-center gap-2">
            <UserCheck className="h-4 w-4"/>
            Entrada Manual
          </h3>

          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
            <div className="sm:col-span-2">
              <Label htmlFor="placa">Placa do Veículo</Label>
              <Input
                id="placa"
                placeholder="ABC-1234"
                value={formData.placa}
                onChange={(e) => setFormData(prev => ({ ...prev, placa: e.target.value.toUpperCase() }))}
                className={cn(
                  formData.placa && "border-green-500 bg-green-50"
                )}
              />
            </div>
            <div className="sm:col-span-2">
              <Label htmlFor="driver">Motorista</Label>
              <Select value={formData.motorista_id} onValueChange={(value) => setFormData(prev => ({ ...prev, motorista_id: value }))}>
                <SelectTrigger className={cn(
                  formData.motorista_id && "border-green-500 bg-green-50"
                )}>
                  <SelectValue placeholder="Selecione o motorista"/>
                </SelectTrigger>
                <SelectContent>
                  {motoristas.map((motorista) => (
                    <SelectItem key={motorista.id} value={motorista.id.toString()}>
                      {motorista.nome}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>

        {/* Video Verification Section */}
        <div className="flex flex-col w-full h-full space-y-4">
          <h3 className="text-md font-medium flex items-center gap-2">
            <CameraIcon className="h-4 w-4"/>
            Entrada por Vídeo
          </h3>

          <div className="relative">
            <video
              ref={videoRef}
              autoPlay
              playsInline
              muted
              className="w-full aspect-video bg-gray-100 rounded-lg object-cover"
              style={{ transform: 'scaleX(-1)' }}
            />
            <canvas ref={canvasRef} className="hidden" />

            {!isStreaming && (
              <div className="absolute inset-0 flex items-center justify-center bg-gray-100 rounded-lg">
                <div className="text-center">
                  <CameraIcon className="h-12 w-12 text-gray-400 mx-auto mb-2"/>
                  <p className="text-gray-500">Câmera desligada</p>
                  <p className="text-xs text-gray-400">Clique em <strong>Iniciar Câmera</strong> para começar</p>
                </div>
              </div>
            )}
          </div>

          {/* Camera Controls */}
          <div className="grid grid-cols-1 gap-2">
            <Button
              onClick={isStreaming ? stopCamera : startCamera}
              variant={isStreaming ? "destructive" : "default"}
              className="flex items-center gap-2"
              disabled={verificationState.loading}
            >
              <CameraIcon className="h-4 w-4"/>
              {isStreaming ? "Parar Câmera" : "Iniciar Câmera"}
            </Button>
          </div>

          {/* Individual Verification Buttons */}
          <div className="grid grid-cols-2 gap-2">
            <Button
              onClick={verifyDriver}
              disabled={!isStreaming || verificationState.loading}
              variant="outline"
              size="sm"
              className="flex items-center gap-2"
            >
              <UserCheck className="h-3 w-3"/>
              Verificar Motorista
            </Button>

            <Button
              onClick={detectPlate}
              disabled={!isStreaming || verificationState.loading}
              variant="outline"
              size="sm"
              className="flex items-center gap-2"
            >
              <Car className="h-3 w-3"/>
              Detectar Placa
            </Button>
          </div>
        </div>

        {/* Verification Status */}
        {(verificationState.driver.verified || verificationState.plate.detected) && (
          <div className="space-y-2">
            <h4 className="text-sm font-medium">Status da Verificação</h4>
            <div className="space-y-2">
              {verificationState.driver.verified && (
                <div className="flex items-center gap-2 p-2 bg-green-50 border border-green-200 rounded">
                  <UserCheck className="h-4 w-4 text-green-600"/>
                  <span className="text-sm text-green-800">
                    Motorista: {verificationState.driver.name}
                    {verificationState.driver.confidence && (
                      <span className="text-xs ml-1">({Math.round(verificationState.driver.confidence * 100)}%)</span>
                    )}
                  </span>
                </div>
              )}

              {verificationState.plate.detected && (
                <div className="flex items-center gap-2 p-2 bg-green-50 border border-green-200 rounded">
                  <Car className="h-4 w-4 text-green-600"/>
                  <span className="text-sm text-green-800">
                    Placa: {verificationState.plate.value}
                  </span>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Verification Complete Status */}
        {verificationComplete && (
          <div className="p-4 bg-green-50 border border-green-200 rounded-lg">
            <div className="flex items-center gap-2">
              <CheckCircleIcon className="h-5 w-5 text-green-600"/>
              <span className="text-green-800 font-medium">Verificação realizada com sucesso!</span>
            </div>
          </div>
        )}

        {/* Active Cycles */}
        {ciclosAbertos.length > 0 && (
          <div className="space-y-3">
            <Label>Ciclos em Andamento</Label>
            <div className="space-y-2">
              {ciclosAbertos.map((ciclo) => (
                <div key={ciclo.id_pesagem} className="flex items-center justify-between p-3 border rounded-lg">
                  <div>
                    <p className="font-medium">{ciclo.placa}</p>
                    <p className="text-sm text-gray-500">{ciclo.motorista}</p>
                  </div>
                  <Button
                    size="sm"
                    variant="outline"
                    disabled={!isConnected || loading}
                    onClick={() => onRegistrarSaida(ciclo.id_pesagem)}
                  >
                    Registrar Saída
                  </Button>
                </div>
              ))}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

const BASE_URL = 'https://14533dbc8cb2.ngrok-free.app/api';

interface ApiResponse<T> {
  data?: T;
  error?: string;
}

class ApiClient {
  private async request<T>(
    endpoint: string,
    options: RequestInit = {}
  ): Promise<ApiResponse<T>> {
    try {
      const response = await fetch(`${BASE_URL}${endpoint}`, {
        headers: {
          'ngrok-skip-browser-warning': 'true',
          ...options.headers,
        },
        ...options,
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        return { error: errorData.error || errorData.erro || `Erro ${response.status}: ${response.statusText}` };
      }

      const data = await response.json();
      return { data };
    } catch (error) {
      console.error('API Error:', error);
      return { error: 'Erro de conexão com o servidor. Verifique sua internet e tente novamente.' };
    }
  }

  async getMotoristas(): Promise<ApiResponse<{ id: number; nome: string }[]>> {
    return this.request('/balanca/motoristas');
  }

  async registrarEntrada(data: {
    placa: string;
    motorista_id: number;
    peso: number;
  }): Promise<ApiResponse<{ id_evento: number }>> {
    return this.request('/balanca/entrada', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    });
  }

  async registrarSaida(data: {
    evento_id: number;
    peso: number;
  }): Promise<ApiResponse<void>> {
    return this.request('/balanca/saida', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    });
  }

  async getCiclosAbertos(): Promise<ApiResponse<{
    id_pesagem: number;
    placa: string;
    motorista: string;
  }[]>> {
    return this.request('/balanca/ciclos-abertos');
  }

  async getHistorico(): Promise<ApiResponse<any[]>> {
    return this.request('/balanca/historico');
  }

  async cadastrarMotorista(formData: FormData): Promise<ApiResponse<any>> {
    return this.request('/cadastro/motorista', {
      method: 'POST',
      body: formData,
    });
  }

  async cadastrarCaminhaoManual(data: {
    placa: string;
    modelo: string;
    empresa: string;
  }): Promise<ApiResponse<{ id_caminhao: number }>> {
    return this.request('/cadastro/caminhao/manual', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    });
  }

  async cadastrarCaminhaoPorImagem(formData: FormData): Promise<ApiResponse<{
    id_caminhao: number;
    placa: string;
  }>> {
    return this.request('/cadastro/caminhao/imagem', {
      method: 'POST',
      body: formData,
    });
  }

  async reconhecimentoCompleto(formData: FormData): Promise<ApiResponse<{
    motorista_reconhecido: boolean;
    motorista_id?: number;
    motorista_nome?: string;
    confianca_motorista?: number;
    placa_reconhecida?: string;
    placa_valida: boolean;
  }>> {
    return this.request('/reconhecimento/completo', {
      method: 'POST',
      body: formData,
    });
  }

  async reconhecerMotorista(formData: FormData): Promise<ApiResponse<{
    motorista_reconhecido: boolean;
    motorista_id?: number;
    motorista_nome?: string;
    confianca?: number;
  }>> {
    return this.request('/reconhecimento/motorista', {
      method: 'POST',
      body: formData,
    });
  }

  async reconhecerPlaca(formData: FormData): Promise<ApiResponse<{
    placa: string;
  }>> {
    return this.request('/reconhecimento/placa', {
      method: 'POST',
      body: formData,
    });
  }

  async getCaminhoes(): Promise<ApiResponse<{
    id_caminhao: number;
    placa: string;
    modelo: string;
    empresa: string;
  }[]>> {
    // Since there's no GET endpoint for trucks, we'll use localStorage as fallback
    // In a real implementation, you'd add a GET /cadastro/caminhoes endpoint to your Python backend
    try {
      const savedTrucks = localStorage.getItem('semensol-trucks');
      const trucks = savedTrucks ? JSON.parse(savedTrucks) : [];
      return { data: trucks };
    } catch (error) {
      return { error: 'Erro ao carregar veículos do armazenamento local' };
    }
  }

  async reconhecerCaminhao(placa: string): Promise<ApiResponse<{
    id_caminhao: number;
    placa: string;
    modelo: string;
    empresa: string;
  }>> {
    const formData = new FormData();
    formData.append('placa', placa);

    return this.request('/reconhecimento/caminhao', {
      method: 'POST',
      body: formData,
    });
  }
}

export const apiClient = new ApiClient();

const BASE_URL = 'https://14533dbc8cb2.ngrok-free.app/api';

interface ApiResponse<T> {
  data?: T;
  error?: string;
  message?: string;
  warning?: string;
}

class ApiClient {
  private async request<T>(
    endpoint: string,
    options: RequestInit = {}
  ): Promise<ApiResponse<T>> {
    try {
      const response = await fetch(`${BASE_URL}${endpoint}`, {
        headers: {
          'ngrok-skip-browser-warning': 'true',
          ...options.headers,
        },
        ...options,
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        return { 
          error: errorData.error || errorData.erro || errorData.message || `Erro ${response.status}: ${response.statusText}` 
        };
      }

      const data = await response.json();
      
      // Check if the response contains a warning message
      if (data && typeof data === 'object' && data.message) {
        return { 
          data, 
          warning: data.message 
        };
      }
      
      return { data };
    } catch (error) {
      console.error('API Error:', error);
      return { error: 'Erro de conexão com o servidor. Verifique sua internet e tente novamente.' };
    }
  }

  async getMotoristas(): Promise<ApiResponse<{ id: number; nome: string }[]>> {
    return this.request('/balanca/motoristas');
  }

  async registrarEntrada(data: {
    placa: string;
    motorista_id: number;
    peso: number;
  }): Promise<ApiResponse<{ id_evento: number }>> {
    return this.request('/balanca/entrada', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    });
  }

  async registrarSaida(data: {
    evento_id: number;
    peso: number;
  }): Promise<ApiResponse<void>> {
    return this.request('/balanca/saida', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    });
  }

  async getCiclosAbertos(): Promise<ApiResponse<{
    id_pesagem: number;
    placa: string;
    motorista: string;
  }[]>> {
    return this.request('/balanca/ciclos-abertos');
  }

  async getHistorico(): Promise<ApiResponse<any[]>> {
    return this.request('/balanca/historico');
  }

  async cadastrarMotorista(formData: FormData): Promise<ApiResponse<any>> {
    return this.request('/cadastro/motorista', {
      method: 'POST',
      body: formData,
    });
  }

  async cadastrarCaminhaoManual(data: {
    placa: string;
    modelo: string;
    empresa: string;
  }): Promise<ApiResponse<{ id_caminhao: number }>> {
    return this.request('/cadastro/caminhao/manual', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    });
  }

  async cadastrarCaminhaoPorImagem(formData: FormData): Promise<ApiResponse<{
    id_caminhao: number;
    placa: string;
  }>> {
    return this.request('/cadastro/caminhao/imagem', {
      method: 'POST',
      body: formData,
    });
  }

  async reconhecimentoCompleto(formData: FormData): Promise<ApiResponse<{
    motorista_reconhecido: boolean;
    motorista_id?: number;
    motorista_nome?: string;
    confianca_motorista?: number;
    placa_reconhecida?: string;
    placa_valida: boolean;
  }>> {
    return this.request('/reconhecimento/completo', {
      method: 'POST',
      body: formData,
    });
  }

  async reconhecerMotorista(formData: FormData): Promise<ApiResponse<{
    motorista_reconhecido: boolean;
    motorista_id?: number;
    motorista_nome?: string;
    confianca?: number;
  }>> {
    return this.request('/reconhecimento/motorista', {
      method: 'POST',
      body: formData,
    });
  }

  async reconhecerPlaca(formData: FormData): Promise<ApiResponse<{
    placa: string;
  }>> {
    return this.request('/reconhecimento/placa', {
      method: 'POST',
      body: formData,
    });
  }

  async getCaminhoes(): Promise<ApiResponse<{
    id: number;
    placa: string;
  }[]>> {
    return this.request('/caminhoes');
  }

  async reconhecerCaminhao(placa: string): Promise<ApiResponse<{
    id_caminhao: number;
    placa: string;
    modelo: string;
    empresa: string;
  }>> {
    const formData = new FormData();
    formData.append('placa', placa);

    return this.request('/reconhecimento/caminhao', {
      method: 'POST',
      body: formData,
    });
  }
}

