"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import {
  Weight,
  CheckCircle,
  Clock,
  ChevronUp,
  ChevronDown,
  ChevronsUpDown,
} from "lucide-react";
import { cn } from "@/lib/utils";

interface WeighingRecord {
  id: number;
  date: string;
  time: string;
  plate: string;
  driver: string;
  company: string;
  cargo: string;
  entryWeight: string;
  exitWeight: string;
  netWeight: string;
  status: string;
}

interface SortConfig {
  key: string;
  direction: "asc" | "desc";
}

interface WeighingTableProps {
  records: WeighingRecord[];
  sortConfig: SortConfig;
  onSort: (key: string) => void;
}

const tableCols = [
  { label: "Data/Hora", key: "date" },
  { label: "Caminhão", key: "plate" },
  { label: "Motorista", key: "driver" },
  { label: "Empresa", key: "company" },
  { label: "Carga", key: "cargo" },
  { label: "Entrada (kg)", key: "entryWeight" },
  { label: "Saída (kg)", key: "exitWeight" },
  { label: "Líquido (kg)", key: "netWeight" },
  { label: "Status", key: "status" },
];

export function WeighingTable({ records, sortConfig, onSort }: WeighingTableProps) {
  const getSortIcon = (columnKey: string) => {
    if (sortConfig.key !== columnKey) {
      return <ChevronsUpDown className="h-4 w-4 text-gray-400" />;
    }
    return sortConfig.direction === "asc" 
      ? <ChevronUp className="h-4 w-4 text-primary-600" />
      : <ChevronDown className="h-4 w-4 text-primary-600" />;
  };

  return (
    <Card className="flex-1">
      <CardHeader>
        <CardTitle className="text-lg font-semibold flex items-center gap-2">
          <Weight className="h-5 w-5 text-primary-600" />
          Registros de Pesagem
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div className="overflow-x-auto rounded-2xl">
          <Table>
            <TableHeader>
              <TableRow>
                {tableCols.map((col, index) => (
                  <TableHead 
                    key={index} 
                    className="cursor-pointer hover:bg-gray-50 select-none"
                    onClick={() => onSort(col.key)}
                  >
                    <div className="flex items-center justify-between">
                      {col.label}
                      {getSortIcon(col.key)}
                    </div>
                  </TableHead>
                ))}
              </TableRow>
            </TableHeader>
            <TableBody>
              {records.map((record) => (
                <TableRow key={record.id}>
                  <TableCell>
                    <div className="text-sm">
                      <div className="font-medium">{record.date}</div>
                      <div className="text-gray-500">{record.time}</div>
                    </div>
                  </TableCell>
                  <TableCell className="font-medium">
                    {record.plate}
                  </TableCell>
                  <TableCell>{record.driver}</TableCell>
                  <TableCell>{record.company}</TableCell>
                  <TableCell>{record.cargo}</TableCell>
                  <TableCell>{record.entryWeight}</TableCell>
                  <TableCell>{record.exitWeight}</TableCell>
                  <TableCell className="font-medium">
                    {record.netWeight}
                  </TableCell>
                  <TableCell>
                    <Badge
                      variant="secondary"
                      className={cn(
                        "flex items-center w-fit space-x-1",
                        record.status === "Concluído"
                        ? "bg-green-100 text-green-700"
                        : "bg-amber-100 text-amber-700"
                      )}
                    >
                      <span>
                        {record.status}
                      </span>
                      {
                        record.status === "Concluído"
                          ? <CheckCircle className="w-4 h-4"/>
                          : <Clock className="w-4 h-4"/>
                      }
                    </Badge>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      </CardContent>
    </Card>
  );
}

"use client";

import { Fragment, useState, useEffect } from "react";
import Link from "next/link";
import { usePathname } from "next/navigation";
import { Dialog } from "@radix-ui/react-dialog";
import { Transition } from "@headlessui/react";
import {
  LayoutDashboard,
  Weight,
  History,
  Truck,
  IdCardLanyard,
  Handshake,
  Settings,
  X, LucideIcon,
} from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import Image from "next/image";

interface NavItem {
  name: string;
  href: string;
  icon: LucideIcon;
}

const navigation: NavItem[] = [
  { name: "Dashboard", href: "/", icon: LayoutDashboard },
  { name: "Pesagem Ativa", href: "/weighing", icon: Weight },
  { name: "Histórico", href: "/history", icon: History },
  { name: "Frota", href: "/fleet", icon: Truck },
  { name: "Motoristas", href: "/drivers", icon: IdCardLanyard },
  { name: "Parceiros", href: "/partners", icon: Handshake },
];

const systemNavigation: NavItem[] = [
  { name: "Configurações", href: "/settings", icon: Settings },
];

interface SidebarProps {
  open: boolean;
  setOpen: (open: boolean) => void;
}

export function Sidebar({ open, setOpen }: SidebarProps) {
  const pathname = usePathname();

  const selectedLinkIndex = navigation.findIndex((item) => item.href === pathname);

  useEffect(() => {
    console.log("teste")
  }, [])

  const NavItem = ({item} : {item: NavItem}) => {
    return (
      <Link
        href={item.href}
        className={cn(
          "flex items-center gap-x-3 py-3.5 px-6",
          "text-sm leading-6 text-gray-700",
          "transition-colors group",
          "hover:text-black hover:bg-gray-100",
          pathname === item.href && "text-primary-600"
        )}
      >
        <item.icon
          className={cn(
            pathname === item.href
              ? "text-primary-600"
              : "text-gray-400 group-hover:text-black",
            "h-5 w-5 shrink-0 transition-colors"
          )}
          aria-hidden="true"
        />
        {item.name}
      </Link>
    )
  }

  return (
    <div className="flex flex-col w-[230px] z-20 fixed min-h-screen gap-y-7 bg-white shadow-lg">
      <div className="flex items-center gap-3 px-6 pt-4 shrink-0">
        <Image
          src="/images/logo.png"
          alt="Logo"
          width={74}
          height={74}
          className="h-10 w-auto"
        />
        <div className="flex flex-col">
          <h1 className="text-xl font-bold text-gray-900">Semensol</h1>
          <p className="text-xs text-gray-500 font-medium">Sistema de pesagem</p>
        </div>
      </div>
      <nav className="flex flex-col h-full gap-y-7 relative">
        <div
          style={{
            top: selectedLinkIndex === 0 ? 32 : (52 * selectedLinkIndex) + 32
          }}
          className="
            absolute bg-primary-100 w-full h-[52px]
            border-l-4 border-primary-500
            transition-all duration-300
            z-0
          "
        />
        <div>
          <div className="text-xs font-semibold leading-6 text-gray-400 uppercase tracking-wider px-6">
            OPERAÇÕES
          </div>
          <div className="mt-2">
            {navigation.map((item) => <NavItem key={item.name} item={item} />)}
          </div>
        </div>
        <div className="mt-auto">
          <div className="text-xs font-semibold leading-6 text-gray-400 uppercase tracking-wider px-6">
            SISTEMA
          </div>
          <div className="mt-2">
            {systemNavigation.map((item) => <NavItem key={item.name} item={item} />)}
          </div>
        </div>
      </nav>
      <div className="flex flex-col mt-auto">
        <div className="text-xs font-semibold leading-6 text-gray-400 uppercase tracking-wider px-6">
          STATUS
        </div>
        <div className="flex items-center gap-3 mb-2 mt-auto px-6 py-4">
          <div className="h-2 w-2 rounded-full bg-green-500"></div>
          <span className="text-sm font-medium text-gray-900">
            Sistema Online
          </span>
        </div>
      </div>
    </div>
  );
}

